changeSet.sql:

CREATE TABLE IF NOT EXISTS trader_details (
    id SERIAL PRIMARY KEY,
    user_id INTEGER NOT NULL,
    profession_id INTEGER NULL,
    mobile_number TEXT NULL,
    description TEXT NULL
);

ALTER TABLE IF EXISTS trader_details
    ADD CONSTRAINT fk_trader_details_user_details_id
    FOREIGN KEY (user_id)
    REFERENCES user_details(id)
    ON DELETE CASCADE;

ALTER TABLE IF EXISTS trader_details
    ADD CONSTRAINT fk_trader_details_profession_type_id
    FOREIGN KEY (profession_id)
    REFERENCES profession_type(id)
    ON DELETE SET NULL;

CREATE INDEX idx_trader_details_user_details_id ON trader_details(user_id);
CREATE INDEX idx_trader_details_profession_type_id ON trader_details(profession_id);
