changeSet.sql:
CREATE TABLE IF NOT EXISTS offer_city ( offer_id INTEGER NOT NULL, city_id INTEGER NOT NULL, wage_per_price_unit DECIMAL(10, 2) NOT NULL, -- base of price measurement created_on TIMESTAMP(3) WITHOUT TIME ZONE NOT NULL, updated_on TIMESTAMP(3) WITHOUT TIME ZONE NOT NULL, PRIMARY KEY (city_id, offer_id) );
ALTER TABLE IF EXISTS offer_city ADD CONSTRAINT fk_offer_city_offer_id FOREIGN KEY (offer_id) REFERENCES offer(id) ON DELETE CASCADE;
ALTER TABLE IF EXISTS offer_city ADD CONSTRAINT fk_offer_city_city_id FOREIGN KEY (city_id) REFERENCES city(id) ON DELETE NO ACTION;
CREATE INDEX IF NOT EXISTS idx_offer_city_offer_id ON offer_city(offer_id);
