changeSet.sql:
CREATE TABLE IF NOT EXISTS trader_details_city ( trader_id INTEGER NOT NULL, city_id INTEGER NOT NULL, created_on TIMESTAMP(3) WITHOUT TIME ZONE NOT NULL, updated_on TIMESTAMP(3) WITHOUT TIME ZONE NOT NULL, PRIMARY KEY (city_id, trader_id) );
ALTER TABLE IF EXISTS trader_details_city ADD CONSTRAINT fk_trader_details_city_trader_details_id FOREIGN KEY (trader_id) REFERENCES trader_details(id) ON DELETE CASCADE;
ALTER TABLE IF EXISTS trader_details_city ADD CONSTRAINT fk_trader_details_city_city_id FOREIGN KEY (city_id) REFERENCES city(id) ON DELETE NO ACTION;
CREATE INDEX IF NOT EXISTS idx_trader_details_city_trader_id on trader_details_city(trader_id);
